from django import formsfrom .  models import *class YangiliklarForm(forms.ModelForm):    class Meta:        model = Yangilik        fields = "__all__"    def save(self, commit=True, **kwargs):        item = super().save(commit=False)        item.rasmlar = kwargs.get('images', [])        item.kelishilgan = kwargs.get('kelishilgan', False)        if commit:            item.save()        return item#class AgroUserForm(forms.ModelForm):    id = forms.CharField(widget=forms.TextInput(attrs={"type": "hidden"}))    class Meta:        model = AgroUser        fields = "__all__"        widgets = {            "password": forms.TextInput(attrs={"type": "hidden"}),            "is_agree": forms.CheckboxInput(attrs={"type": "hidden"}),            "number": forms.TextInput(attrs={"type": "text", "placeholder": "99893 3888010"}),            # "user_type": forms.Select(attrs={"class": "js-example-basic-single"}),            "email": forms.TextInput(attrs={"type": "text", "placeholder": "...@gmail.com"}),            "username": forms.TextInput(attrs={"type": "text", "placeholder": "Хасанов Руслан Мидхатович"}),        }    def save(self, commit=True, **kwargs):        item = super().save(commit=False)        if commit:            item.save()        return item